function isDarkTheme(){return"undefined"!=typeof Storage&&"false"!==localStorage.getItem("darkTheme")}function setDarkTheme(e){e?(document.body.className="dark-theme","undefined"!=typeof Storage&&localStorage.setItem("darkTheme","true")):(document.body.className="","undefined"!=typeof Storage&&localStorage.setItem("darkTheme","false"))}function updateThemeSwitchText(){isDarkTheme()?document.getElementById("theme-switch").innerHTML="Switch to Light Theme":document.getElementById("theme-switch").innerHTML="Switch to Dark Theme"}function onThemeSwitchClick(){setDarkTheme(!isDarkTheme()),window.location.reload()}function refreshTheme(){setDarkTheme(isDarkTheme())}function formatNumber(e){return new Intl.NumberFormat("en-US").format(e)}